class MyHash < Hash
  def initialize
    super{|h, k| h[k] = MyHash.new}
  end
end

def create_http_server(path, body)
  t = Thread.new do
    server = WEBrick::HTTPServer.new :Port => 12342
    server.mount_proc path do |req, res|
      res.body = body
    end
    server.start
  end
  sleep 1
  yield
  t.kill
end
